import React, { useState, useEffect } from "react";
import products from "/src/components/assets/fake-data/products";
import { useParams } from "react-router-dom";
import Helmet from "../components/Helmet/Helmet";
import CommonSection from "../components/UI/common-section/CommonSection";
import { Container, Row, Col } from "reactstrap";
import ExtraIngredient from "../components/ExtraIngredient/ExtraIngredient";
import { useDispatch } from "react-redux";
import { cartActions } from "../store/shopping-cart/cartSlice";
import { useSelector } from "react-redux";

import "../styles/product-details.css";
import "../styles/product-card.css";

import ProductCard from "../components/UI/product-card/ProductCard";

const ExtraIngredients = {
  COGUMELOS: "Cogumelos",
  CEBOLA: "Cebola",
  PIMENTA: "Pimenta",
  ABACAXI: "Abacaxi",
  ATUM: "Atum",
  CARNE: "Carne",
  QUEIJO: "Queijo",
  MOLHO: "Molho Picante",
  MILHO: "Milho",
};

const PizzaDetails = () => {
  const { id } = useParams();
  const dispatch = useDispatch();
  const [extraIngredients, setExtraIngredients] = useState([]);
  const [isUpdateNotificationDisplayed, setIsUpdateNotificationDisplayed] =
    useState(false);
  const product = products.find((product) => product.id === id);
  const cartProducts = useSelector((state) => state.cart.cartItems);
  const [previewImg, setPreviewImg] = useState(product.image01);
  const { title, price, category, image01 } = product;
  const relatedProduct = products.filter(
    (item) => category === item.category
  );

  useEffect(() => {
    const existingPizza = cartProducts.find((item) => item.id === id);
    if (existingPizza) {
      setExtraIngredients(existingPizza.extraIngredients);
    } else {
      setExtraIngredients([]);
    }
  }, [cartProducts, id]);

  const addItem = () => {
    setIsUpdateNotificationDisplayed(true);
    setTimeout(function () {
      setIsUpdateNotificationDisplayed(false);
    }, 3000);

    dispatch(
      cartActions.addItem({
        id,
        title,
        price,
        image01,
        extraIngredients,
      })
    );
  };

  useEffect(() => {
    setPreviewImg(product.image01);
    window.scrollTo(0, 0);
  }, [product]);

  function updateExtraIngredients(ingredient) {
    if (extraIngredients.includes(ingredient)) {
      setExtraIngredients(extraIngredients.filter((item) => item !== ingredient));
    } else {
      setExtraIngredients((previousState) => [...previousState, ingredient]);
    }
  }

  return (
    <Helmet title="Product-details">
      {isUpdateNotificationDisplayed && (
        <div className="updateCartNotifiation">
          <span>Seu pedido foi atualizado!!</span>
        </div>
      )}

      <CommonSection title={title} />

      <section>
        <Container>
          <Row>
            <Col lg="2" md="2">
              <div className="product__images">
                <div
                  className="img__item mb-3"
                  onClick={() => setPreviewImg(product.image01)}
                >
                  <img src={product.image01} alt="" className="w-50" />
                </div>
                <div
                  className="img__item mb-3"
                  onClick={() => setPreviewImg(product.image02)}
                >
                  <img src={product.image02} alt="" className="w-50" />
                </div>

                <div
                  className="img__item"
                  onClick={() => setPreviewImg(product.image03)}
                >
                  <img src={product.image03} alt="" className="w-50" />
                </div>
              </div>
            </Col>

            <Col lg="4" md="4">
              <div className="product__main-img">
                <img src={previewImg} alt="" className="w-100" />
              </div>
            </Col>

            <Col lg="6" md="6">
              <div className="single__product-content">
                <h2 className="product__title mb-3">{title}</h2>
                <p className="product__price">
                  {" "}
                  Preco: <span>R${price}</span>
                </p>
                <p className="category mb-5">
                  Categoria: <span>{category}</span>
                </p>

                <button onClick={addItem} className="addTOCART__btn">
                  {cartProducts.find((item) => item.id === id)
                    ? "Atualizar"
                    : "Adicionar ao carrinho"}
                </button>
              </div>
            </Col>

            <Col lg="12">
              <div className="extraIngredientsGrid">
                {Object.values(ExtraIngredients).map((ingredient) => {
                  return (
                    <ExtraIngredient
                      isChecked={extraIngredients.includes(ingredient)}
                      key={ingredient}
                      onSelect={updateExtraIngredients}
                      ingredient={ingredient}
                    ></ExtraIngredient>
                  );
                })}
              </div>
            </Col>

            <Col lg="12">
              <div className="description__content">
                {/* Coloque a descrição do produto aqui */}
              </div>
            </Col>

            <Col lg="12" className="mb-5 mt-4">
              <h2 className="related__Product-title">
                Voce tambem pode gostar
              </h2>
            </Col>

            {relatedProduct.map((item) => (
              <Col
                lg="3"
                md="4"
                sm="6"
                xs="6"
                className="mb-4"
                key={item.id}
              >
                <ProductCard item={item} />
              </Col>
            ))}
          </Row>
        </Container>
      </section>
    </Helmet>
  );
};

export default PizzaDetails;
